#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

const int neutral = 0;
int comp(int a, int b) {
	return max(a, b);
}

class SegmentTree {
	vector<int> a;
	int n;
public:
	SegmentTree(int sz) {
		for (n = 1; n < sz; n <<= 1);
		a.assign(n << 1, neutral);
	}
	void update(int i, int x) {
		a[i+n] = comp(a[i+n], x); //substitui
		for (i += n, i >>= 1; i; i >>= 1)
			a[i] = comp(a[i<<1], a[1+(i<<1)]);
	}
	int query(int l, int r) {
		int ans = neutral;
		for (l += n, r += n+1; l < r; l >>= 1, r >>= 1) {
			if (l & 1) ans = comp(ans, a[l++]);
			if (r & 1) ans = comp(ans, a[--r]);
		}
		return ans;
	}
};

int t[MAXN];
int lis1[MAXN], lis2[MAXN], lis3[MAXN];
map<int, int> reduce;

int main() {
	int n, x;
	scanf("%d %d", &n, &x);
	FOR1(i, n) {
		scanf("%d", &t[i]);
		reduce[t[i]] = 0;
		reduce[t[i]+x] = 0;
	}
	int cnt = 0;
	for(auto & pp : reduce) {
		pp.se = cnt++;
	}
	int ans = 0;
	SegmentTree st(cnt);
	FOR1(i, n) {
		lis1[i] = 1 + st.query(0, reduce[t[i]]-1);
		st.update(reduce[t[i]], lis1[i]);
		ans = max(ans, lis1[i]);
		//printf("lis1[%d] = %d\n", i, lis1[i]);
	}
	st = SegmentTree(cnt);
	REP1(i, n) {
		lis2[i] = 1 + st.query(reduce[t[i]]+1, cnt-1);
		st.update(reduce[t[i]], lis2[i]);
		//printf("lis2[%d] = %d\n", i, lis2[i]);
	}
	st = SegmentTree(cnt);
	FOR1(i, n) {
		lis3[i] = 1 + st.query(0, reduce[t[i]+x]-1);
		st.update(reduce[t[i]], lis1[i]);
		ans = max(ans, lis2[i] + lis3[i] - 1);
	}
	printf("%d\n", ans);
	return 0;
}