#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef vector<int> vi;
typedef long long ll;
typedef pair<ll, int> ii;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;

bool vis[MAXN];
ii nxt[MAXN], prv[MAXN];

ii getNext(int i) {
	if (!vis[i]) return ii(0, i);
	ii cur = getNext(nxt[i].se);
	return nxt[i] = ii(cur.fi + nxt[i].fi, cur.se);
}

ii getPrev(int i) {
	if (!vis[i]) return ii(0, i);
	ii cur = getPrev(prv[i].se);
	return prv[i] = ii(cur.fi + prv[i].fi, cur.se);
}

int main() {
	int n, s;
	scanf("%d %d", &n, &s);
	FOR(i, n) {
		int u = i;
		int v = (i+1)%n;
		ll d;
		scanf("%lld", &d);
		nxt[u] = ii(d, v);
		prv[v] = ii(d, u);
	}
	s--;
	ll ans = 0;
	mset(vis, false);
	FOR(cnt, n) {
		ii l = getPrev(s);
		ii r = getNext(s);
		ii cur = min(l, r);
		ans += cur.fi;
		s = cur.se;
		vis[s] = true;
	}
	printf("%lld\n", ans);
	return 0;
}