#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 100009
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

struct inter {
	int id, L, ans;
};

bool lcomp(inter a, inter b) {
	return a.l < b.l;
}

bool idcomp(inter a, inter b) {
	return a.id < b.id;
}

int n, q;
inter a[MAXN];

int dist[4*MAXN];
int nchanges[4*MAXN];
vector<ii> changes[4*MAXN];
#define left(u) (2*u)
#define right(u) (2*u+1)

void push(int u, int l, int r) {
	if (changes[u].empty()) return;
	
}

void update(int u, int l, int r, int a, int b, ii v) {
	push(u, l, r);
	if (r < a || b < l) return;
	if (a <= l && r <= b) {
		changes[u].pb(v);
		return;
	}
	int m = (l + r) / 2;
	update(left(u), l, m, a, b, v);
	update(right(u), m+1, r, a, b, v);
}

void solve(int u, int l, int r, int dsum, int nsum) {
	push(l, r);
	dsum += dist[u];
	nsum += nchanges[u];
	if (l == r) {
		a[r].ans = dsum - a[i].L*nsum;
		return;
	}
	solve(left(u), l, m, a, b, v);
	solve(right(u), m+1, r, a, b, v);
}

int main() {
	while(scanf("%d %d", &n, &q) != EOF) {
		FOR(i, n) {
			scanf("%d", &a[i].L);
			a[i].id = i;
		}
		sort(a, a+n, lcomp);

	}
}