#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define MAXLOGN 20
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x))
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

int n, m, k, l;
const int POP = 1;
const int PUSH = 2;

struct event {
	int time;
	int type;
};

bool operator <(event a, event b) {
	if (a.time != b.time) return a.time < b.time;
	return a.type < b.type;
}

set<event> events;

int qsize;
int ans;
void process(event ev) {
	events.erase(ev);
	if (ev.type == POP) {
		qsize--;
		if (qsize < 0) {
			ans = 0;
			events.clear();
			return;
		}
		event nev = ev;
		nev.time += l;
		if (nev.time <= k && qsize > 0) events.insert(nev);
	}
	if (ev.type == PUSH) {
		int cur = qsize*l + (l - (ev.time%l));
		ans = min(ans, cur);
		//printf("enter before %d, wait time = %d\n", ev.time, cur);
		qsize++;
	}
	//printf("time %d, %s, qsize = %d\n", ev.time, ev.type == PUSH ? "PUSH" : "POP", qsize);
}

int main() {
	int T;
	scanf("%d", &T);
	FOR1(caseNo, T) {
		scanf("%d %d %d %d", &n, &m, &k, &l);

		event ev;
		ev.type = PUSH;
		FOR(i, n) {
			scanf("%d", &ev.time);
			events.insert(ev);
		}
		ev.time = k;
		events.insert(ev);

		ev.type = POP;
		ev.time = l;
		events.insert(ev);

		qsize = m;
		ans = (m+1)*l;
		while(!events.empty()) {
			event ev = *events.begin();
			process(ev);
		}

		printf("%d\n", ans);
	}
	return 0;
}