#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 2000009
#define MAXM 900009
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

void zfunction (char* s, int* z) {
	int n = strlen(s);
	fill(z, z+n, 0);
	for (int i=1, l=0, r=0; i<n; ++i) {
		if (i <= r)
			z[i] = min(r-i+1, z[i-l]);
		while (i+z[i] < n && s[z[i]] == s[i+z[i]])
			++z[i];
		if (i+z[i]-1 > r)
			l = i,  r = i+z[i]-1;
	}
}

int k;
char P[MAXN], T[MAXN], aux[MAXN];
int z1[MAXN], z2[MAXN];

int main() {
	scanf(" %s %s %d", T, P, &k);
	int n = strlen(T);
	int m = strlen(P);
	strcpy(aux, P);
	strcat(aux, T);
	zfunction(aux, z1);
	reverse(P, P+m);
	reverse(T, T+n);
	strcpy(aux, P);
	strcat(aux, T);
	zfunction(aux, z2);
	reverse(z2+m, z2+m+n);
	vi ans;
	FOR(it, n) {
		if (it+m-1 >= n) break;
		int i = it+m;
		z1[i] = min(z1[i], m);
		z2[i] = min(z2[i], m);
		int cur = z1[i] + z2[i+m-1];
		cur = min(cur, m);
		if (cur + k >= m) ans.pb(it+1);
	}
	printf("%u\n", ans.size());
	for(int i : ans) printf("%d ", i);
	return 0;
}