#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200009
#define MAXM 100009
#define ALFA 256
#define MOD 1000000009
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

class KMP{
private:
	char P[MAXN];
	int m, n, b[MAXN];
public:
	KMP(const char* _P) {
		strcpy(P, _P);
		b[0] = -1;
		m = strlen(P);
		for(int i = 0, j = -1; i < m;) {
			while (j >= 0 && P[i] != P[j]) j = b[j];
			i++; j++;
			b[i] = j;
		}
	}
	int match(const char* T) {
		n = strlen(T);
		int ans = 0;
		for (int i=0, j=0; i < n;) {
			while (j >= 0 && T[i] != P[j]) j = b[j];
			i++; j++;
			if (j == m) {
				ans++;
				j = b[j];
			}
		}
		return ans;
	}
};

KMP kmp("");

int match(char *s1, char *s2) {
	kmp = KMP(s2);
	int ans = kmp.match(s1);
	return ans;
}

char suf[3][MAXN], pre[3][MAXN], aux[MAXN];
ll ans[3];

char f[MAXN];
int k;

int main() {
	scanf("%s %d", f, &k);
	int n = strlen(f);
	//FOR(i, n) f[i] = tolower(f[i]);
	strcpy(suf[0], ""); strcpy(pre[0], "");
	strcpy(suf[1], "a"); strcpy(pre[1], "a");
	strcpy(suf[2], "b"); strcpy(pre[2], "b");
	ans[0] = 0;
	ans[1] = match(suf[1], f);
	ans[2] = match(suf[2], f);
	for(int i = 3; i <= k; i++) {

		//pre
		strcpy(pre[i%3], pre[(i+1)%3]);
		strcat(pre[i%3], pre[(i+2)%3]);
		pre[i%3][n] = 0;

		//suf
		strcpy(suf[i%3], suf[(i+1)%3]);
		strcat(suf[i%3], suf[(i+2)%3]);
		int sz = strlen(suf[i%3]);
		int pos = sz - n;
		if (pos > 0) rotate(suf[i%3], suf[i%3] + pos, suf[i%3] + sz);
		suf[i%3][n] = 0;

		//ans
		bool ok = true;
		strcpy(aux, suf[(i+1)%3]);
		int sz1 = strlen(aux);
		if (sz1 >= n) {
			rotate(aux, aux+sz1-n+1, aux+sz1);
			sz1 = n-1; aux[sz1] = 0;
		}
		strcat(aux, pre[(i+2)%3]);
		int sz2 = strlen(aux) - sz1;
		if (sz2 >= n) {
			sz2 = n-1; aux[sz2+sz1] = 0;
		}
		ans[i%3] = (ans[(i+1)%3] + ans[(i+2)%3] + match(aux, f)) % MOD;
		assert(ans[i%3] >= 0);
	}
	printf("%lld\n", ans[k%3]);
	return 0;
}