#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 100009
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x))
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

vector<int> primes;
bitset<MAXN> bs;
int pw[MAXN];

int g(int p, int k) {
	if (p == 1) return 1;
	return (k==0) - (k==1);
	//int q = 1;
	//for(int i = 1; i < k; i++) q *= p;
	//return q*(p-1);
}

void sieve(int n, int f[]) {
	bs.set(); bs[0] = bs[1] = 0;
	primes.clear(); f[1] = g(1, 1);
	for (ll i = 2; i <= n; i++) {
		if (bs[i]) {
			primes.push_back(i);
			f[i] = g(i, 1); pw[i] = 1;
		}
		for (int j = 0; j < primes.size() && i*1ll*primes[j] <= n; j++) {
			bs[i * primes[j]] = 0;
			if (i % primes[j] == 0) {
				int pwr = 1;
				for(int k = 0; k < pw[i]; k++) pwr *= primes[j];
				f[i * primes[j]] = f[i / pwr] * g(primes[j], pw[i]+1);
				pw[i * primes[j]] = pw[i] + 1;
				break;
			} else {
				f[i * primes[j]] = f[i] * f[primes[j]];
				pw[i * primes[j]] = 1;
			}
		}
		//if (i < 20) printf("mu[%lld] = %d\n", i, f[i]);
	}
}

int n, m;
int mu[MAXN];

int solve(int r, bool inclusive) {
	if (r == 0) return 0;
	int ans = 0;
	for(int k = 1; k <= r; k++) {
		if (mu[k] == 0) continue;
		for(int p = k; p <= n-1 && p <= r; p += k) {
			ll maxq = sqrt(r*1ll*r - p*1ll*p);
			if (!inclusive && maxq*maxq + p*1ll*p == r*1ll*r) maxq--;
			if (maxq > m-1) maxq = m-1;
			if (maxq < 0) maxq = 0;
			maxq -= maxq%k;
			int cur1 = (mu[k]*(n-p) + MOD) % MOD;
			int cur2 = (m*(maxq/k) - (k+maxq)*(maxq/k)/2) % MOD;
			ans = (ans + cur1*2ll*cur2) % MOD;
		}
	}
	if (r > 1 || inclusive) {
		ans = (ans + (n*(m-1ll))%MOD) % MOD;
		ans = (ans + (m*(n-1ll))%MOD) % MOD;
	}
	//printf("solve(%d) = %d\n", r, ans);
	return ans;
}

int main() {
	sieve(MAXN-1, mu);
	int l, r;
	while(scanf("%d %d %d %d", &n, &m, &l, &r) != EOF) {
		printf("%d\n", (solve(r, true) - solve(l, false) + MOD) % MOD);
	}
	return 0;
}