#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 109
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x))
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

int mex[MAXN][MAXN];

int main() {
	FOR(i, MAXN) {
		FOR(j, MAXN) {
			if (i == 0 || j == 0 || i == j) {
				//printf("%4d ", -1);
				continue;
			}
			vi used;
			for(int u = min(i, j)-1; u > 0; u--) {
				used.pb(mex[i-u][j-u]);
			}
			for(int u = i-1; u > 0; u--) {
				if (i-u == j) continue;
				used.pb(mex[i-u][j]);
			}
			for(int u = j-1; u > 0; u--) {
				if (i == j-u) continue;
				used.pb(mex[i][j-u]);
			}
			sort(all(used));
			used.resize(unique(all(used)) - used.begin());
			mex[i][j] = 0;
			while(mex[i][j] < sz(used) && used[mex[i][j]] == mex[i][j]) mex[i][j]++;
			//printf("%4d ", mex[i][j]);
		}
		//printf("\n");
	}

	int n;
	while(scanf("%d", &n) != EOF) {
		int ans = 0;
		bool ok = true;
		FOR(k, n) {
			int i, j;
			scanf("%d %d", &i, &j);
			ans ^= mex[i][j];
			if (i == 0 || j == 0 || i == j) ok = false;
		}
		if (!ok || ans != 0) printf("Y\n");
		else printf("N\n");
	}
	return 0;
}