#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 1009
#define MAXM 900009
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef vector<int> vi;
typedef pair<int, int> ii;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;

int par[MAXN], id[MAXN], n;
vi g[MAXN];
bool app[MAXN];

bool comp(int i, int j) {
	return g[i].size() < g[j].size();
}

int main() {
	scanf("%d", &n);
	FOR1(u, n) {
		int k;
		scanf("%d", &k);
		FOR(j, k) {
			int v;
			scanf("%d", &v);
			g[u].pb(v);
		}
		id[u] = u;
	}
	sort(id+1, id+1+n, comp);
	mset(par, -1);
	mset(app, false);
	bool ok = true;
	FOR1(ut, n) {
		int u = id[ut];
		for(int v : g[u]) app[v] = true;
		for(int v : g[u]) {
			if (par[v] == -1) {
				par[v] = u;
				app[v] = false;
				for(int w : g[v]) {
					if (!app[w]) ok = false;
					app[w] = false;
				}
			}
		}
		for(int v : g[u]) {
			if (app[v]) ok = false;
		}
	}
	int cnt = 0;
	FOR1(u, n) {
		if (par[u] == -1) cnt++;
	}
	if (cnt != 1) ok = false;
	if (ok) {
		printf("YES\n");
		FOR1(u, n) {
			if (par[u] != -1) printf("%d %d\n", par[u], u);
		}
	}
	else printf("NO\n");
	return 0;
}