#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

bool vis[2][MAXN];
int n, m;

void dfs(int i, int j) {
	vis[i][j] = true;
	if (!vis[1-i][j]) dfs(1-i, j);
	else if (j+1 < n && !vis[i][j+1]) dfs(i, j+1);
}

map<int, int> reduce;
int x[MAXN], y[MAXN];

int main() {
	while(scanf("%d %d", &n, &m) != EOF) {
		mset(vis, false);
		reduce.clear();
		FOR(j, m) {
			scanf("%d %d", &x[j], &y[j]);
			x[j]--; y[j]--;
			reduce[y[j]] = 0;
		}
		reduce[n] = 0;
		int last = -1, lastp;
		for(auto & u : reduce) {
			if (last == -1) {
				u.second = 2 + (u.first % 2);
			}
			else if (u.first == last+1) {
				u.second = lastp + 1;
			}
			else if ((u.first - last) % 2 == 0) {
				u.second = lastp + 2;
			}
			else u.second = lastp + 3;

			last = u.first;
			lastp = u.second;
		}
		FOR(j, m) {
			y[j] = reduce[y[j]];
			vis[x[j]][y[j]] = true;
		}
		n = reduce[n];
		dfs(0, 0);
		bool ok = true;
		FOR(i, n) {
			if (!vis[0][i] || !vis[1][i]) ok = false;
		}
		if (ok) printf("Yes\n");
		else printf("No\n");
	}
	return 0;
}