#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 509
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

int n;
char buffer[10];
bool ask(int r1, int c1, int r2, int c2) {
	if (r1 == 1 && c1 == 1 && r2 == n && c2 == n) return true;
	if (r1 == n && c1 == n && r2 == 1 && c2 == 1) return true;
	assert(abs(r1-r2)+abs(c1-c2) >= n-1);
	printf("? %d %d %d %d\n", r1, c1, r2, c2);
	fflush(stdout);
	scanf(" %s", buffer);
	if (!strcmp(buffer, "BAD")) exit(0);
	if (!strcmp(buffer, "YES")) return true;
	if (!strcmp(buffer, "NO")) return false;
	return false;
}

inline bool valid(int i, int j) {
	return i >= 1 && i <= n && j >= 1 && j <= n;
}

bool vis1[MAXN][MAXN];
int midi, midj;
string en;
bool bt1(int i, int j, bool need) {
	if (!valid(i, j)) return false;
	if (vis1[i][j]) return false;
	vis1[i][j] = true;
	if (need && !ask(1, 1, i, j)) return false;
	if (i+j == n+1) {
		midi = i;
		midj = j;
		return true;
	}
	if (bt1(i-1, j, true)) {
		en.pb('D'); return true;
	}
	if (bt1(i, j-1, false)) {
		en.pb('R'); return true;
	}
	return false;
}

bool vis2[MAXN][MAXN];
string st;
bool bt2(int i, int j, bool need) {
	if (!valid(i, j) || i > midi || j > midj) return false;
	if (vis2[i][j]) return false;
	vis2[i][j] = true;
	if (i == midi && j == midj) return true;
	if (need && !ask(i, j, n, n)) return false;
	if (bt2(i, j+1, true)) {
		st.pb('R'); return true;
	}
	if (bt2(i+1, j, false)) {
		st.pb('D'); return true;
	}
	return false;
}

int main() {
	scanf("%d", &n);
	mset(vis1, false);
	mset(vis2, false);
	bt1(n, n, false);
	bt2(1, 1, false);
	reverse(all(st));
	printf("! %s%s\n", st.c_str(), en.c_str());
	fflush(stdout);
	return 0;
}