#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 1000009
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

int n, m, a[MAXN], b[MAXN];
int x[2*MAXN];
bool taken[2*MAXN], done[MAXN];
vi g[2*MAXN];

bool take(int d, int u, int j) {
	if (done[u]) return true;
	if (taken[j] || x[j] > d) return false;
	//printf("%d taking day %d\n", u, x[j]);
	taken[j] = true;
	done[u] = true;
	for(int v : g[j]) {
		if (u == v) continue;
		if (!take(d, v, a[v]+b[v]-j)) return false;
	}
	return true;
}

bool check(int d) {
	//printf("d = %d\n", d);
	mset(done, false);
	mset(taken, false);
	FOR1(i, n) {
		if (done[i]) continue;
		if (x[a[i]] > d) return false;
		if (x[b[i]] > d) {
			if (!take(d, i, a[i])) return false;
		}
	}
	FOR1(j, m) {
		if (x[j] > d || taken[j]) continue;
		int cnt = 0;
		for(int v : g[j]) {
			if (!done[v]) cnt++;
		}
		if (cnt == 1) {
			for(int v : g[j]) {
				if (!done[v] && !take(d, v, j)) return false;
			}
		}
	}
	FOR1(i, n) {
		if (done[i]) continue;
		if (taken[a[i]] && taken[b[i]]) return false;
		else if (!taken[a[i]]) {
			if (!take(d, i, a[i])) return false;
		}
		else if (!taken[b[i]]) {
			if (!take(d, i, b[i])) return false;
		}
	}
	return true;
}

int main() {
	scanf("%d", &n);
	map<int, int> reduce;
	FOR1(i, n) {
		scanf("%d %d", &a[i], &b[i]);
		reduce[a[i]] = 0;
		reduce[b[i]] = 0;
	}
	m = 0;
	for(auto & pp : reduce) {
		pp.se = ++m;
		x[m] = pp.fi;
	}
	FOR1(i, n) {
		a[i] = reduce[a[i]];
		b[i] = reduce[b[i]];
		g[a[i]].pb(i);
		g[b[i]].pb(i);
	}
	int lo = 0;
	int hi = INF;
	while(hi > lo + 1) {
		int mid = (hi + lo) / 2;
		if (check(mid)) hi = mid;
		else lo = mid;
	}
	printf("%d\n", hi == INF ? -1 : hi);
	return 0;
}