#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define MAXLOGN 20
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x))
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

set< pair<ll,int> > pos[MAXN];
vi g[MAXN];
int s[MAXN];
ll S, w[MAXN], off[MAXN];
int n, L;

void merge(int u, int v) {
	if (pos[v].size() > pos[u].size()) {
		swap(pos[u], pos[v]);
		swap(off[u], off[v]);
	}
	for(auto pp : pos[v]) {
		ll curw = pp.fi + off[v] - off[u];
		pos[u].insert({curw, pp.se});
	}
	pos[v].clear();
}

void dfs(int u, int h) {
	s[u] = 0;
	off[u] = 0;
	for(int v : g[u]) {
		dfs(v, h+1);
		s[u] += s[v];
		off[v] += w[u];
		merge(u, v);
	}

	while(!pos[u].empty()) {
		auto cur = *pos[u].rbegin();
		if (cur.fi+off[u] > S) pos[u].erase(cur);
		else break;
	}
	while(!pos[u].empty()) {
		auto cur = *pos[u].rbegin();
		if (cur.se-h > L-1) pos[u].erase(cur);
		else break;
	}
	if (pos[u].empty()) {
		s[u]++;
		pos[u].insert({w[u], h});
		off[u] = 0;
	}
}

int main() {
	scanf("%d %d %lld", &n, &L, &S);
	bool ok = true;
	FOR1(u, n) {
		scanf("%lld", &w[u]);
		if (w[u] > S) ok = false;
	}
	for(int i = 2; i <= n; i++) {
		int p;
		scanf("%d", &p);
		g[p].pb(i);
	}
	dfs(1, 0);
	printf("%d\n", ok ? s[1] : -1);
	return 0;
}