using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

int n;
ll s;

ll mincost(int b, ll h = 1, ll left = -1) {
	ll cost = 0;
	if (left == -1) left = n;
	ll m = 1;
	while(left > 0) {
		ll am = min(m, left);
		left -= am;
		cost += h*am;
		h++;
		m *= b;
	}
	return cost;
}

int getminbfactor() {
	if (n*(n+1LL)/2 == s) return 1;
	int lo = 1;
	int hi = n;
	while(hi > lo + 1) {
		int mid = (hi + lo) / 2;
		ll cost = mincost(mid);
		if (cost > s) lo = mid;
		else hi = mid;
	}
	return hi;
}

int cnt = 0, par[MAXN];

void bfs(int b) {
	ll h = 1, m = 1;
	while(1 + n*1ll*h + n*(n-1LL)/2 > s || n*1ll*h + n*(n+1LL)/2 < s) {
		ll am = min(m, n);
		n -= am;
		s -= h*am;
		h++;
		m *= b;
	}
}

bool check(int h, int n, ll s, int b, int maxb) {
	int rn = n%b;
	int rs = s%b;
	int cnt1 = min(rn, sn);
	int cnt2 = max(rn, sn) - cnt1;
	int cnt3 = b - cnt2;
	if (cnt1 > 0 && minfactor(maxb, h+1, n/b+1) > s/b+1) return false;
	if (cnt2 > 0 && rn > rs && minfactor(maxb, h+1, n/b+1) > s/b) return false;
	if (cnt2 > 0 && rn < rs && minfactor(maxb, h+1, n/b) > s/b+1) return false;
	if (cnt3 > 0 && minfactor(maxb, h+1, n/b) > s/b) return false;
	return true;
}

