#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200009
#define MAXM 40009
#define ALFA 26
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

vector< ii > g[MAXN];
map<string, int> red;
int cnt = 0, nds = 0;
int pos[MAXM][ALFA];
int reduce(string s) {
	if(!red.count(s)) {
		red[s] = ++cnt;
		FOR(c, ALFA) {
			pos[cnt][c] = ++nds;
			g[nds].clear();
		}
	}
	return red[s];
}

int dist[MAXN];
int dijkstra(int s, int t) {
	set<ii> pq;
	mset(dist, INF);
	FOR(c, ALFA) {
		dist[pos[s][c]] = 0;
		pq.insert({dist[pos[s][c]], pos[s][c]});
	}
	while(!pq.empty()) {
		int u = pq.begin()->se;
		pq.erase(pq.begin());
		for(ii pv : g[u]) {
			int w = pv.fi;
			int v = pv.se;
			if (dist[v] > dist[u] + w) {
				if (pq.count({dist[v], v})) pq.erase({dist[v], v});
				dist[v] = dist[u] + w;
				pq.insert({dist[v], v});
			}
		}
	}
	int ans = INF;
	FOR(c, ALFA) {
		ans = min(ans, dist[pos[t][c]]);
	}
	return ans;
}

int main() {
	int M;
	while(cin >> M, M) {
		red.clear(); cnt = nds = 0;
		string ss, st;
		cin >> ss;
		int s = reduce(ss);
		cin >> st;
		int t = reduce(st);
		FOR(e, M) {
			string su, sv, word;
			cin >> su >> sv >> word;
			int u = reduce(su);
			int v = reduce(sv);
			FOR(i, ALFA) {
				if (i == word[0] - 'a') continue;
				int j = word[0] - 'a';
				g[pos[u][i]].pb({int(word.size()), pos[v][j]});
				g[pos[v][i]].pb({int(word.size()), pos[u][j]});
			}
		}
		int ans = dijkstra(s, t);
		if (ans == INF) cout << "impossivel\n";
		else cout << ans << endl;
	}
	return 0;
}