#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 2009
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

class UnionFind {
private:
	vector<int> parent, rank;
public:
	UnionFind(int N) {
		rank.assign(N+9, 0);
		parent.assign(N+9, 0);
		for (int i = 0; i < N+9; i++) parent[i] = i;
	}
	int find(int i) {
		while(i != parent[i]) i = parent[i];
		return i;
	}
	bool isSameSet(int i, int j) {
		return find(i) == find(j);
	}
	void unionSet (int i, int j) {
		if (isSameSet(i, j)) return;
		int x = find(i), y = find(j);
		if (rank[x] > rank[y]) parent[y] = x;
		else {
			parent[x] = y;
			if (rank[x] == rank[y]) rank[y]++;
		}
	}
};

vector< pair<ll, ii> > edgeList; // (weight, two vertices) of the edge
bool connected[MAXN];

ll kruskal(int N) {
	ll cost = 0;
	UnionFind UF(N);
	pair<int, ii> edge;
	sort(edgeList.begin(), edgeList.end());
	for (auto edge : edgeList) {
		int u = UF.find(edge.second.first);
		int v = UF.find(edge.second.second);
		if (u != v && (!connected[u] || !connected[v])) {
			cost += edge.first;
			UF.unionSet(u, v);
			connected[u] = connected[v] = (connected[u] || connected[v]);
		}
	}
	return cost;
}


int main() {
	int T;
	scanf("%d", &T);
	FOR1(caseNo, T) {
		int N, M, K;
		scanf("%d %d %d", &N, &M, &K);
		mset(connected, false);
		edgeList.clear();
		FOR(i, K) {
			int p;
			scanf("%d", &p);
			connected[p] = true;
		}
		FOR(j, M) {
			int u, v;
			ll w;
			scanf("%d %d %lld", &u, &v, &w);
			edgeList.pb({w, {u, v}});
		}
		printf("Case #%d: %lld\n", caseNo, kruskal(N));
	}
}