#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 50009
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

map<int, map<int, int> > galaxy;
int x[MAXN], y[MAXN];
vi g[MAXN];
bool vis[MAXN];

ii dfs(int u) {
	if (vis[u]) return {0, 0};
	vis[u] = true;
	ii ans(1, 0);
	for(int v : g[u]) {
		ii cur = dfs(v);
		ans.fi += cur.se;
		ans.se += cur.fi;
	}
	return ans;
}

int main() {
	int n;
	while(scanf("%d", &n) != EOF) {
		galaxy.clear();
		FOR1(i, n) {
			scanf("%d %d", &x[i], &y[i]);
			g[i].clear();
			auto & mx = galaxy;
			for(auto itx = mx.lower_bound(x[i]-5); itx != mx.end() && itx->fi <= x[i]+5; itx++) {
				auto & my = itx->se;
				for(auto ity = my.lower_bound(y[i]-5); ity != my.end() && ity->fi <= y[i]+5; ity++) {
					int j = ity->se;
					if ((x[i]-x[j])*1ll*(x[i]-x[j]) + (y[i]-y[j])*1ll*(y[i]-y[j]) <= 25) {
						g[i].pb(j);
						g[j].pb(i);
						//printf("%d and %d belong to different galaxies\n", i, j);
					}
				}
			}
			galaxy[x[i]][y[i]] = i;
		}
		mset(vis, false);
		int ans = 0;
		FOR1(u, n) {
			if (!vis[u]) {
				ii cur = dfs(u);
				ans += min(cur.fi, cur.se);
			}
		}
		printf("%d\n", ans);
	}
	return 0;
}