#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 109
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

//dp[l][r] = numero de formacoes considerando o circulo com o trecho [l,r]
int n, dp[MAXN][MAXN];
char s[MAXN];

int DP(int l, int r) {
	if (dp[l][r] >= 0) return dp[l][r]; //ja calculei, so retorno a resposta
	int i = l; //vamos ver todas as possibilidades de triangulos para o vertice i = l
	dp[l][r] = 0;
	for(int j = i+1; j <= r; j++) { //testo todos os j maiores para o segundo ponto
		for(int k = j+1; k <= r; k++) { //testo todos os k seguintes para o terceiro ponto
			int nRed = 0;
			if (s[i] == 'R') nRed++;
			if (s[j] == 'R') nRed++;
			if (s[k] == 'R') nRed++;
			if (nRed > 1) continue; //verifico se o triangulo eh valido
			int cur = 1;
			if (j > i+1) cur *= DP(i+1, j-1); //numero de possibilidades do trecho de i a j
			if (k > j+1) cur *= DP(j+1, k-1); //numero de possibilidades do trecho de j a k
			if (r > k) cur *= DP(k+1, r); //numero de possibilidades do trecho de k a r
			dp[l][r] += cur;
		}
	}
	return dp[l][r];
}

int main() {
	int T;
	scanf("%d", &T);
	FOR1(t, T) {
		scanf("%d %s", &n, s);
		//setando dp como -1 pra todo mundo
		mset(dp, -1); //voce sabe porque memset funciona com -1?
		printf("Case %d: %d\n", t, DP(0, n-1));
	}
	return 0;
}