#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

char P[MAXN], ans[MAXN];
int m, b[MAXN];

void KMP() {
	b[0] = -1;
	m = strlen(P);
	for(int i = 0, j = -1; i < m;) {
		while (j >= 0 && P[i] != P[j]) j = b[j];
		i++; j++;
		b[i] = j;
	}
}

bool check(int i, char & c) {
	char cur = P[i];
	while(i > 0 && cur == P[i]) i = b[i];
	c = P[i];
	return i > 0;
}

bool build() {
	int n = 0;
	for(int i = 0; i < m; i++) {
		char carac;
		if (check(i, carac)) {
			int j = i;
			while(j >= 0 && carac != P[j]) j = b[j];
			assert(j > 0);
			for(; j < m; j++) {
				ans[n++] = P[j];
			}
			ans[n] = 0;
			return true;
		}
		else ans[n++] = P[i];
	}
	ans[n] = 0;
	return false;
}

int main() {
	int T;
	scanf("%d", &T);
	FOR1(caseNo, T) {
		scanf(" %s", P);
		KMP();
		printf("Case #%d: %s\n", caseNo, build() ? ans : "Impossible");
	}
	return 0;
}
