    int encode(vector<vector<int>>& board) {
        vector<int> pos(6);
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < 3; j++) {
                pos[board[i][j]] = 3*i+j;
            }
        }
        int state = 0;
        for (int i = 5; i >= 0; i--) {
            state = (i+1)*state + pos[i];
            for (int j = i-1; j >= 0; j--) {
                if (pos[j] > pos[i]) {
                    pos[j]--;
                }
            }
        }
        return state;
    }
public:
    int slidingPuzzle(vector<vector<int>>& board) {
        return ans[encode(board)];
    }
