#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 200309
#define MAXM 900009
#define MAXLOGN 20
#define ALFA 256
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define sz(x) int(x.size())
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x))
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;
typedef vector<int> vi;
typedef pair<int, int> ii;

string apply(string s, int c, int i) {
	if (i > 4) i -= 4;
	switch(i) {
		case 1: return s + (c ? "1" : "0");
		case 2: return (c ? "1" : "0") + s;
		case 3: return s + (c ? "0" : "1");
		case 4: return (c ? "0" : "1") + s;
		default: assert(false);  
	}
	return "";
}

int getc(string &s, int i) {
	if (i > 4) i -= 4;
	int bit;
	switch(i) {
		case 1:
			bit = s[s.size()-1]-'0'; s.pop_back(); 
			return bit;
		case 2:
			bit = s[0]-'0'; s = s.substr(1); 
			return bit;
		case 3:
			bit = s[s.size()-1]-'0'; s.pop_back(); 
			return bit^1;
		case 4:
			bit = s[0]-'0'; s = s.substr(1); 
			return bit^1;
		default: assert(false);  
	}
	return 0;
}

int getnxt(int L) {
	return ((L+6) % 8) + 1;
}

string solve1(int L, ll mask) {
	string s;
	FOR(i, L) {
		ll bit = (mask&(1<<i)) ? 1 : 0;
		mask ^= (bit<<i);
		if (i == 0) {
			s = bit ? "1" : "0";
		}
		else {
			int j = getnxt(i+1);
			if (bit && j > 4) {
				mask ^= ((1LL<<L)-1)^((1LL<<(i+1))-1);
			}
			s = apply(s, bit, j);
		}
	}
	return s;
}

ll solve2(int L, string s) {
	ll mask = 0;
	REP(i, L) {
		if (i == 0) {
			mask ^= (s[0]-'0');
		}
		else {
			int j = getnxt(i+1);
			ll bit = getc(s, j);
			if (bit && j > 4) {
				mask ^= ((1LL<<L)-1)^((1LL<<(i+1))-1);
			}
			mask ^= (bit<<i);
		}
	}
	return mask;
}

int main() {
	int q;
	scanf("%d", &q);
	while(q --> 0) {
		char op;
		scanf(" %c", &op);
		if (op == 'H') {
			ll mask;
			int L;
			scanf("%d %lld", &L, &mask);
			mask--;
			printf("%s\n", solve1(L, mask).c_str());
		}
		if (op == 'I') {
			char buffer[100];
			scanf(" %s", buffer);
			string s(buffer);
			int L = s.size();
			printf("%lld\n", solve2(L, s)+1);
		}
	}
	return 0;
}