#include <bits/stdc++.h>
#define DEBUG false
#define debugf if (DEBUG) printf
#define MAXN 309
#define MAXM 5000009
#define MOD 1000000007
#define INF 0x3f3f3f3f
#define INFLL 0x3f3f3f3f3f3f3f3f
#define EPS 1e-9
#define PI 3.141592653589793238462643383279502884
#define FOR(x,n) for(int x=0; (x)<int(n); (x)++)
#define FOR1(x,n) for(int x=1; (x)<=int(n); (x)++)
#define REP(x,n) for(int x=int(n)-1; (x)>=0; (x)--)
#define REP1(x,n) for(int x=(n); (x)>0; (x)--)
#define pb push_back
#define pf push_front
#define fi first
#define se second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define mset(x,y) memset(&x, (y), sizeof(x));
using namespace std;
typedef vector<int> vi;
typedef pair<int, int> ii;
typedef long long ll;
typedef unsigned long long ull;
typedef long double ld;
typedef unsigned int uint;

vi g[MAXN];
int n, m;
char s[MAXM], trans[MAXN][MAXN];
bool vis[MAXN], cycle[MAXN], hastrans[MAXN];
char ans[2][MAXM];

bool dfs(int u, int t) {
	if (vis[u]) return false;
	vis[u] = true;
	if (u == t) return true;
	for(int v : g[u]) {
		if (dfs(v, t)) return true;
	}
	return false;
}

vector<char> toposort;

void ts(int u) {
	if (vis[u]) return;
	vis[u] = true;
	for(int v : g[u]) {
		ts(v);
	}
	toposort.pb(u);
}

int main() {
	scanf("%d", &m);
	FOR(j, m) {
		char u;
		scanf(" %c", &u);
		scanf(" %s", trans[u]);
		hastrans[u] = true;
		n = strlen(trans[u]);
		FOR(i, n) {
			g[u].pb(trans[u][i]);
		}
	}
	FOR(u, MAXN) {
		mset(vis, false);
		cycle[u] = false;
		for(int v : g[u]) {
			if (dfs(v, u)) cycle[u] = true;
		}
	}

	scanf("%d %s", &n, s);
	FOR(i, n) {
		if (cycle[s[i]]) {
			printf("-1\n");
			return 0;
		}
	}

	mset(vis, false);
	FOR(u, MAXN) {
		ts(u);
	}
	reverse(all(toposort));

	int it = 0;
	strcpy(ans[it], s);
	for(int u : toposort) {
		if (!hastrans[u]) continue;
		//printf("replacing %c -> %s\n", u, trans[u]);
		int k = 0;
		for(int i = 0; ans[it][i]; i++) {
			if (ans[it][i] == u) {
				for(int j = 0; trans[u][j]; j++) {
					ans[it^1][k++] = trans[u][j];
				}
			}
			else ans[it^1][k++] = ans[it][i];
		}
		ans[it^1][k] = 0;
		it ^= 1;
		//printf("result (%d): %s\n", k, ans[it]);
	}

	printf("%s\n", ans[it]);
	return 0;
}